import streamlit as st

def create_header():
        st.write("""
        <style>
        .header {
            font-size: 24px;
            font-weight: bold;
            text-align: left;
            padding: 10px;
            background-color: #f0f2f6;
            color: #333;
        }
        .logout-btn {
            text-align: right;
        }
        </style>
        """, unsafe_allow_html=True)
def html_template():
    html_template = """
        <style>
        .right-corner-image {
            position: absolute;
            top: 1;
            right: 1;
            margin: 10px;
        }
        </style>
        <img src="data:image/jpeg;base64," class="right-corner-image" width="100">
        """
    return html_template
def sidebar_style():
    sidebar_style = """
    <style>
    /* Targeting the sidebar more specifically */
    [data-testid="stSidebar"] > div:first-child {
        background-color: #3cb371 !important;
    }
    /* Attempting to target all text within the sidebar to change its color to white */
    [data-testid="stSidebar"] .css-1d391kg, [data-testid="stSidebar"] .st-cb, [data-testid="stSidebar"] .st-dd, [data-testid="stSidebar"] {
        color: #ffffff !important;
    }

    /* If the above doesn't cover all text, this broader rule might help */
    [data-testid="stSidebar"] * {
        color: #ffffff !important;
    }
    </style>
    """
    return sidebar_style